CREATE TABLE profiles (id BIGINT AUTO_INCREMENT, type VARCHAR(50), first_name VARCHAR(50), middle_name VARCHAR(50), last_name VARCHAR(50), email VARCHAR(50), password VARCHAR(50), status VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX type_idx (type), INDEX first_name_idx (first_name), INDEX middle_name_idx (middle_name), INDEX last_name_idx (last_name), INDEX email_idx (email), PRIMARY KEY(id)) ENGINE = INNODB;
